import React, { useEffect } from 'react';
import PopupWithForm from './PopupWithForm';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function EditProfilePopup(props) {
  const currentUser = React.useContext(CurrentUserContext);

  const [name, setName] = React.useState('');
  const [description, setDescription] = React.useState('');

  useEffect(() => {
    setName(currentUser.name);
    setDescription(currentUser.about);
  }, [currentUser]);

  function handleSubmit(e) {
    // Запрещаем браузеру переходить по адресу формы
    e.preventDefault();

    // Передаём значения управляемых компонентов во внешний обработчик
    props.onUpdateUser({
      name,
      about: description,
    });
  } 

  const handleNameChange = (evt) => {
    setName(evt.target.value);
  }

  const hanldeDescriptionChange = (evt) => {
    setDescription(evt.target.value);
  }

  return (
    <PopupWithForm
      name="edit"
      title="Редактировать профиль"
      buttonText="Сохранить"
      isOpen={props.isOpen}
      onClose={props.onClose}
      // onSubmit={handleSubmit}
    >
      <input className="form__item form__item_el_name"
        id="profile-name" 
        type="text" name="name" 
        placeholder="Имя" 
        minLength="2" maxLength="40"
        value={name}
        onChange={handleNameChange}
        required 
      />
      <p className="form__error" id="profile-name-error"></p>
      <input className="form__item form__item_el_bio"
        id="profile-bio" 
        type="text" name="about" 
        placeholder="О себе" 
        minLength="2" maxLength="200" 
        value={description}
        onChange={hanldeDescriptionChange}
        required 
      />
      <p className="form__error" id="profile-bio-error"></p>
    </PopupWithForm>
  )
}

export default EditProfilePopup;
